def splitTape(tape):
    putTogetherTape = ""
    for i in tape:
        if(i is True):
            putTogetherTape += "1"
        else:
            putTogetherTape += "0"
    
    return putTogetherTape


    

    
        
def countFrequnces(tape):
    splitZeros = splitTape(tape).split("1")
    zeroDict = {}
    for i in splitZeros:
        if(i == ''):
            continue
        if(zeroDict.keys().__contains__(i)):
            zeroDict[i] += 1
        else:
            zeroDict[i] = 1
    
    splitOnes = splitTape(tape).split("0")
    for i in splitOnes:
        if(i == ''):
            continue
        if(zeroDict.keys().__contains__(i)):
            zeroDict[i] += 1
        else:
            zeroDict[i] = 1
    
    return zeroDict
    



def countFreq(rleCoded):
    freqDict = {}
    for i in rleCoded:
        if(i == ''):
            continue
        if(freqDict.keys().__contains__(i)):
            freqDict[i] += 1
        else:
            freqDict[i] = 1
    return freqDict




