def differential_encode_deltas(deltas):
    if len(deltas) < 2:
        return deltas
    
    diff_deltas = [deltas[0]]  # Keep first delta as-is
    
    for i in range(1, len(deltas)):
        diff = deltas[i] - deltas[i-1]
        diff_deltas.append(diff)
    
    return diff_deltas

        
# Add this to your compression code (deltaOptimized.py)
def cap_deltas(deltas, max_delta=32):
    result = []
    for delta in deltas:
        if delta >= 0:
            # Handle positive deltas
            while delta > max_delta:
                result.append(max_delta)
                delta -= max_delta
            if delta > 0:
                result.append(delta)
        else:
            # Handle negative deltas
            while delta < -max_delta:
                result.append(-max_delta)
                delta += max_delta
            if delta < 0:
                result.append(delta)
    return result

# Use CAPPED instead of QUANTIZED
seed = 350  # Use the same seed
finalTape = encode()  # Make sure encode takes seed parameter
finalTapeString = "".join("1" if bit else "0" for bit in finalTape)

