# Use CAPPED deltas (not quantized!)
capped_deltas = cap_deltas(raw_deltas, 256)
print(f"Capped deltas: {len(capped_deltas)} deltas")
print(f"Sample capped deltas: {capped_deltas[:10]}")

# Compress with Huffman
codec = dahuffman.HuffmanCodec.from_frequencies(countFreq(capped_deltas))
print(len(capped_deltas))
print(codec.print_code_table())
compressed_data = codec.encode(capped_deltas)

# Save everything
